/* Function: mdlInitializeSampleTimes =========================================
 * Abstract:
 *    Specifiy  the sample time.
 */
static void mdlInitializeSampleTimes(SimStruct *S)
{
    ssSetSampleTime(S, 0, SAMPLE_TIME_0);
    ssSetOffsetTime(S, 0, 0.0);
}

#define MDL_INITIALIZE_CONDITIONS
 /* Function: mdlInitializeConditions ========================================
  * Abstract:
  *    Initialize the states
  */
 static void mdlInitializeConditions(SimStruct *S)
 {
   #define MDL_INITIALIZE_CONDITIONS
/* Function: mdlInitializeConditions ============================
* Abstract:
* Initialize both continuous states to zero
*/ 
    real_T *lastTime = (real_T*) ssGetDWork(S,0);
    int_T *newCycle = (int_T*) ssGetDWork(S,1);
    real_T *tempPMax = (real_T*) ssGetDWork(S,2);
    real_T *tempPhiAtPMax = (real_T*) ssGetDWork(S,3);
    real_T *newPMax = (real_T*) ssGetDWork(S,4);
    real_T *newPhiAtPMax = (real_T*) ssGetDWork(S,5);
        

    /* Initialize the dwork to 0 */
    lastTime[0] = -1;  // time at the last step
    newCycle[0] = 1;  // ignition delay
    tempPMax[0] = 60e5;  // temporary pMax
    newPMax[0] = 60e5;   // new pMax
    tempPhiAtPMax[0] = 0;
    newPhiAtPMax[0] = 0;
}

