/* Function: mdlOutputs =======================================================
 *
*/
static void mdlOutputs(SimStruct *S, int_T tid)
{
    real_T        *pMax  = (real_T *)ssGetOutputPortRealSignal(S,0);
    real_T        *phiAtPMax  = (real_T *)ssGetOutputPortRealSignal(S,1);
    const real_T *newPMax = (const real_T*) ssGetDWork(S,4);
    const real_T *newPhiAtPMax = (const real_T*) ssGetDWork(S,5);
    pMax[0] = newPMax[0];
    phiAtPMax[0] = newPhiAtPMax[0];
}


#define MDL_UPDATE  /* Change to #undef to remove function */
/* Function: mdlUpdate ======================================================
   * Abstract:
   *    This function is called once for every major integration time step.
   *    Discrete states are typically updated here, but this function is useful
   *    for performing any tasks that should only take place once per
   *    integration step.
   */
static void mdlUpdate(SimStruct *S, int_T tid)
{
    const real_T   *t  = (const real_T*) ssGetInputPortSignal(S,0);
    const real_T   *phi  = (const real_T*) ssGetInputPortSignal(S,1);
    const real_T   *p  = (const real_T*) ssGetInputPortSignal(S,2);
    real_T *lastTime = (real_T*) ssGetDWork(S,0);
    int_T   *newCycle = (int_T*) ssGetDWork(S,1);
    real_T *tempPMax = (real_T*) ssGetDWork(S,2);
    real_T *tempPhiAtPMax = (real_T*) ssGetDWork(S,3);
    real_T *newPMax = (real_T*) ssGetDWork(S,4);
    real_T *newPhiAtPMax = (real_T*) ssGetDWork(S,5);
    pMaxFinder_Update_wrapper(t, phi, p, lastTime, newCycle, tempPMax, 
            tempPhiAtPMax, newPMax, newPhiAtPMax);
}



